<?php defined('SYSPATH') OR die('No direct access allowed.'); ?><h2><?php echo $module_menu; ?></h2><form class="form_details" action="<?php echo URL::site('admin-cp/'.$class_name.'/add'); ?>" method="post" enctype="multipart/form-data">	<div class="form_wrapper">        <div class="form_row">            <label>Subject</label>            <input type="text" name="subject" id="subject" class="required" value="<?php echo $fields['subject']; ?>" />        	<?php echo sprintf($errors['subject'], 'Subject'); ?>		</div>		        <div class="form_row">            <label>Name</label>            <input type="text" name="name" id="name" class="required" value="<?php echo $fields['name']; ?>" />        	<?php echo sprintf($errors['name'], 'Name'); ?>		</div>		<div class="form_row">            <label>Description</label>            <textarea name="description" id="description" class="required description"/><?php echo $fields['description']; ?></textarea>            <?php echo sprintf($errors['description'], 'Description'); ?>		</div>			<?php if (isset($show_upload) && $show_upload) : ?>		<?php 		foreach ($uploads as $row_name => $row_params) : ?>            <fieldset style="clear:both;">                <legend><strong><?php echo $row_params['label']; ?></strong></legend>				<?php //echo sprintf($errors[$row_name], $row_params['label']); ?>                <div class="form_row">                    <label><?php echo $row_params['label']; ?></label>                    <input type="file" name="<?php echo $row_name; ?>" id="<?php echo $row_name; ?>" />                    <?php if (isset($row_params['note']) && $row_params['note'] != '') : ?>                        <div class="form_row">                            <label>&nbsp;</label>                            <?php echo HTML::chars($row_params['note'], TRUE); ?>                        </div>                    <?php endif; ?>                </div>                <?php if (isset($row_params['caption']) && $row_params['caption']) : ?>                <div class="form_row">                    <label>Caption</label>                    <input type="text" name="<?php echo $row_name.'_caption'; ?>" id="<?php echo $row_name.'_caption'; ?>" value="<?php echo $fields[$row_name.'_caption']; ?>" />                </div>                <?php endif; ?>    		</fieldset>        <?php 		endforeach; 		?>        <?php endif; ?>		<?php if (isset($show_visible) && $show_visible) : ?>		<div class="form_row">				<?php echo Form::label('visible'); ?><div>Only to Members</div>			<?php echo Form::select('visible', $visible, $fields['visible'], array('id'=>'visible'));?>		</div>			<?PHP endif;?>        <?php echo sprintf($errors['status'], 'Status'); ?>        <div class="form_row">            <label>Status</label>            <select name="status" id="status" class="required">                <option value="">&nbsp;</option>                <?php foreach ($statuses as $row) : ?>                <option value="<?php echo $row; ?>" <?php echo ($fields['status'] == $row) ? 'selected="selected"' : ''; ?>><?php echo HTML::chars(ucfirst($row), TRUE); ?></option>                <?php endforeach; ?>            </select>        </div>        <div class="form_row">            <label>&nbsp;</label>            <input type="checkbox" name="add_another" id="add_another" value="TRUE" /> <label for="add_another" class="sub_label">Add another <?php echo ucwords(str_replace('_', ' ', $class_name)); ?></label>        </div>	</div>	<div class="form_row">		<label>&nbsp;</label>		<input type="submit" class="btn_add" value="" />	</div></form>