<?php defined('SYSPATH') OR die('No direct access allowed.');class Model_Media extends Model_Database {		protected $table = 'media'; 	protected $tbl_name;	protected $_model_vars;	protected $db;     protected $prefix;	protected static $_instance;		public function __construct () {		parent::__construct();		$this->_model_vars	= array('id'				=> 0,									'release_id'		=> 0,									'name'				=> '',									'subject'			=> '',									'description'		=> '',									'allow_comment'		=> 0,									'visible'			=> 0,									'user_id'			=> 0,									'count'				=> 0,									'order'				=> 0,									'status'			=> '',									'added'				=> 0,									'modified'			=> 0);		$this->db		= Database::instance();		$this->tbl_name	= $this->table;        $this->prefix   = $this->db->table_prefix() ? $this->db->table_prefix() : ''; 		$this->table	= (!empty($this->prefix)) ? $this->prefix . $this->table : $this->table;	}		public static function instance () {		if (self::$_instance === NULL)			self::$_instance	= new self();		return self::$_instance;	}		public function install () {		$insert_data	= FALSE;		if (!$this->db->list_tables($this->table))                 $insert_data	= TRUE;		$sql	= 'CREATE TABLE IF NOT EXISTS `'.$this->table.'` ('				. '`id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY, '				. '`release_id` INT(11) UNSIGNED NULL, '				. '`name` VARCHAR(64) NULL, '				. '`subject` VARCHAR(64) NULL, '				. '`description` TEXT NULL, '				. '`allow_comment` TINYINT(1) NULL, '				. '`visible` TINYINT(1) NULL, '				. '`user_id` TINYINT(3) NULL , '				. '`count` INT(11) NULL , '					. '`order` INT(11) NULL , '					. '`status` ENUM(\'publish\', \'unpublish\', \'draft\', \'deleted\') NULL DEFAULT \'draft\', '				. '`added` INT(11) NULL, '				. '`modified` INT(11) NULL, '				. 'INDEX (`release_id`, `name`, `status`) '				. ') ENGINE=MYISAM';		$this->db->query('CREATE',$sql);		// Insert Default Data		if ($insert_data) {			$sql	= 'INSERT INTO `'.$this->table.'` '				. '(`id`,`release_id`,`name`,`subject`,`description`,`allow_comment`,`visible`,`user_id`,`count`,`order`,`status`,`added`,`modified`) '				. 'VALUES '				. '(1, 1, \'vn-021\', \'VN 021\', \'Nama-Nya Imanuel-Allah Menyertai Kita\', 0, 1, 0, 1, 1, \'publish\', '.time().', 1350665407), '				. '(2, 1, \'vn-022\', \'VN 022\', \'Domba yang Hilang\', 0, 1, 0, 1, 2, \'publish\', '.time().', 1350665363), '				. '(3, 1, \'vn-022\', \'VN 023\', \'Ingatlah akan Penciptamu pada Masa Mudamu\', 0, 1, 0, 1, 3, \'publish\', '.time().', 1350665384), '				. '(4, 1, \'vn-023\', \'VN 024\', \'Dan kepada Petrus\', 0, 1, 0, 1, 4, \'publish\', '.time().', 1350806246), '				. '(5, 1, \'vn-024\', \'VN 025\', \'Penghukuman Bagi yang Membenarkan Diri dan Pembenaran Bagi yang Merendahkan Diri\', 0, 1, 0, 1, 5, \'publish\', '.time().', 1350668653);';			$this->db->query('INSERT',$sql);		}		return $this->db->list_tables($this->table);	}		public function load ($id = '') {		$return_object	= TRUE;		if ($id == '') {			$return_object	= FALSE;			$id				= $this->id;		}		$objects	= $this->find(array('id' => $id), '', 1);		if (count($objects) == 1) {			if ($return_object) {				return $objects[0];			} else {				$vars	= array_keys($this->_model_vars);				foreach ($vars as $var) {					$this->$var	= $objects[0]->$var;				}				return TRUE;			}		}		return FALSE;	}		public function load_by_name ($name, $release_id = '') {		if ($release_id != '') {			$where_cond	= array('name'			=> $name,								'release_id'	=> $release_id,								'status !='		=> 'deleted');		} else {			$where_cond	= array('name'			=> $name,								'status !='		=> 'deleted');		}		if ($this->find_count($where_cond) == 0)			unset($where_cond['status !=']);		$objects		= $this->find($where_cond, '', 1);		return (isset($objects[0])) ? $objects[0] : FALSE;	}		public function set_order($parent_id = '', $group = '', $action='') {		$type = '';		$action = strtolower($action);		if (!empty($parent_id)) {			$type = 'WHERE `release_id` = '. $parent_id .'';		} 		// query to find default data 		$sql = 'SELECT '.$action.'(`order`) as `'.$action.'_order` FROM `'.$this->table.'` '.$type.';';		$order	= $this->manual_query($sql,TRUE);		$return = $action.'_order';		if (!empty($order[0]->$return))			return $order[0]->$return;	}		public function add ($params = '') {		if (!is_array($params)) return;		$params['added']	= time();		unset($this->_model_vars['id']);		$params	= array_merge($this->_model_vars, $params);		$query = DB::insert($this->tbl_name, array_keys($params))->values(array_values($params))->execute();		if (count($query) !== FALSE)			$insert_id	= mysql_insert_id();		else			return FALSE;		return $insert_id;	}		public function update () {		$this->modified	= time();		$object_vars	= get_object_vars($this);		unset($object_vars['_model_vars'], $object_vars['db']);		$object_vars = Arr::overwrite($this->_model_vars,$object_vars);		$result = DB::update($this->tbl_name)->set($object_vars)->where('id', '=', $this->id)->execute();		return $result;	}		public function delete ($id = '') {		if ($id == '')			$id	= $this->id;		$result		= DB::delete($this->tbl_name)->where('id', '=', $id)->execute();		return $result;	}		public function find ($where_cond = '', $order_by = '', $limit = '', $offset = '') {		/** Build where query **/		if ($where_cond != '') {			if (is_array($where_cond) && count($where_cond) != 0) {				$buffers	= array();				$operators	= array('LIKE',									'IN',									'!=',									'>=',									'<=',									'>',									'<',									'=');				foreach ($where_cond as $field => $value) {					$operator	= '=';					if (strpos($field, ' ') != 0)						list($field, $operator)	= explode(' ', $field);					if (in_array($operator, $operators)) {						$field		= '`'.$field.'`';						if ($operator == 'IN' && is_array($value))							$buffers[]	= $field.' '.$operator.' (\''.implode('\', \'', $value).'\')';						else							$buffers[]	= $field.' '.$operator.' \''.$value.'\'';					} else if (is_numeric($field)) {						$buffers[]	= $value;					} else {						$buffers[]	= $field.' '.$operator.' \''.$value.'\'';					}				}                				$where_cond	= implode(' AND ', $buffers);                   			}		}		$sql_order = ''; 		if ($order_by != '') {			$sql_order = ' ORDER BY'; 			$i 	   = 1;			foreach ($order_by as $order => $val) {				$split = ($i > 1) ? ', ' : ''; 				$sql_order .= ''. $split .' `'. $order.'` '.$val.' ';				$i++;			}			$order_by  = $sql_order;		}		$sql_limit = ''; 		if ($limit != '' && $offset != '') {			$offset    = $offset . ','; 			$sql_limit = ' LIMIT '. $offset . $limit; 		}		else if ($limit != '') {			$sql_limit = ' LIMIT '. $limit; 		}		$limit = $sql_limit;		if ($where_cond != '') {			$rows = $this->db->query(Database::SELECT, 'SELECT * FROM `'.$this->table.'` WHERE '. $where_cond . $order_by . $limit, TRUE)->as_array();		}		else {			$rows = $this->db->query(Database::SELECT, 'SELECT * FROM `'.$this->table.'`' . $order_by . $limit, TRUE)->as_array();		}		$returns	= array();		foreach ($rows as $row) {			$object			= new Model_Media;			$object_vars	= get_object_vars($row);			unset($object_vars['_model_vars'], $object_vars['db']);			foreach ($object_vars as $var => $val) {				$object->$var	= $val;			}			$returns[]		= $object;			unset($object, $vars);		}		return $returns;	}		public function find_count ($where_cond = '') {		/** Build where query **/		if ($where_cond != '') {			if (is_array($where_cond) && count($where_cond) != 0) {				$buffers	= array();				$operators	= array('LIKE',									'IN',									'!=',									'>=',									'<=',									'>',									'<',									'=');				foreach ($where_cond as $field => $value) {					$operator	= '=';					if (strpos($field, ' ') != 0)						list($field, $operator)	= explode(' ', $field);					if (in_array($operator, $operators)) {						$field		= '`'.$field.'`';						if ($operator == 'IN' && is_array($value))							$buffers[]	= $field.' '.$operator.' (\''.implode('\', \'', $value).'\')';						else							$buffers[]	= $field.' '.$operator.' \''.$value.'\'';					} else if (is_numeric($field)) {						$buffers[]	= $value;					} else {						$buffers[]	= $field.' '.$operator.' \''.$value.'\'';					}				}				$where_cond	= implode(' AND ', $buffers);			}			$rows = $this->db->query(Database::SELECT, 'SELECT * FROM `'.$this->table.'` WHERE '. $where_cond, TRUE)->count();		} else {			$rows = sizeof($this->find());		}		return $rows;	}		public function manual_query ($query = '',$object=FALSE) {		if($query == '')			return FALSE;		$query = $this->db->query(Database::SELECT, $query, $object);		if($query !== FALSE)			return $query;		else			return FALSE;		}}