<?php defined('SYSPATH') OR die('No direct access allowed.'); ?><h2><?php echo $module_menu; ?></h2><?php echo Form::open(URL::site('admin-cp/'.$module_name.'/add'), array('method'=>'POST', 'class'=>'form_details','enctype'=>'multipart/form-data'));?>	<div class="form_wrapper">        <div class="form_row">            <label for="subject">Subject</label>        	<?php echo Form::input('subject', $fields['subject'], array('class'=>'required','id'=>'subject'));?>			<?php echo sprintf($errors['subject'], 'Subject'); ?>		</div>		        <div class="form_row">            <label for="name">Name</label>        	<?php echo Form::input('name', $fields['name'], array('class'=>'required','id'=>'name'));?>			<?php echo sprintf($errors['name'], 'Name'); ?>		</div>        <?php echo sprintf($errors['release_id'], 'Release'); ?>        <div class="form_row">            <label>Release</label>            <select name="release_id" id="release_id" class="required">                <option value="">&nbsp;</option>                <?php foreach ($releases as $row) : ?>                <option value="<?php echo $row->id; ?>" <?php echo ($row->id == $fields['release_id']) ? 'selected="selected"' : ''; ?>><?php echo HTML::chars($row->subject, TRUE); ?></option>                <?php endforeach; ?>            </select>        </div>		<div class="form_row">            <label for="description">Description</label>			<?php echo Form::textarea('description', $fields['description'], array('class'=>'required description'));?>            <?php echo sprintf($errors['description'], 'Description'); ?>		</div>			<?php if (isset($show_upload) && $show_upload) : ?>		<?php 		foreach ($uploads as $row_name => $row_params) : 			?>            <fieldset style="clear:both;">                <legend><strong><?php echo $row_params['label']; ?></strong></legend>				<div class="form_row">                    <label><?php echo $row_params['label']; ?></label>					<?php echo sprintf($errors[$row_name], $row_params['label']); ?>                    <input type="file" name="<?php echo $row_name; ?>" class="<?php echo ($row_params['optional']) ? '' : 'required'; ?>" id="<?php echo $row_name; ?>" />                    <?php if (isset($row_params['note']) && $row_params['note'] != '') : ?>                        <div class="form_row">                            <label>&nbsp;</label>                            <?php echo HTML::chars($row_params['note'], TRUE); ?>                        </div>                    <?php endif; ?>                </div>                <?php if (isset($row_params['caption']) && $row_params['caption']) : ?>                <div class="form_row">                    <label>Caption</label>                    <input type="text" name="<?php echo $row_name.'_caption'; ?>" id="<?php echo $row_name.'_caption'; ?>" value="<?php echo $fields[$row_name.'_caption']; ?>" />                </div>                <?php endif; ?>    		</fieldset>        <?php 		endforeach; 		?>        <?php endif; ?>		<?php if (isset($show_visible) && $show_visible) : ?>		<div class="form_row">				<?php echo Form::label('visible'); ?>			<?php echo Form::select('visible', $visible, $fields['visible'], array('id'=>'visible'));?>			<span>Only to Members</span>		</div>			<?PHP endif;?>		<?php if (isset($show_allow_comment) && $show_allow_comment) : ?>        <?php echo sprintf($errors['allow_comment'], 'Allow Comment'); ?>        <div class="form_row">            <label for="allow_comment">Allow User to Comment?</label>            <input type="checkbox" name="allow_comment" id="allow_comment" value="1" <?php echo ($fields['allow_comment'] == 1) ? 'checked="checked"' : ''; ?> /> <label for="allow_comment" class="sub_label">Yes, user can comment this content</label>        </div>        <?php endif; ?>        <?php echo sprintf($errors['status'], 'Status'); ?>        <div class="form_row">			<?php echo Form::label('status'); ?>            <select name="status" id="status" class="required">                <option value="">&nbsp;</option>                <?php foreach ($statuses as $row) : ?>                <option value="<?php echo $row; ?>" <?php echo ($fields['status'] == $row) ? 'selected="selected"' : ''; ?>><?php echo HTML::chars(ucfirst($row), TRUE); ?></option>                <?php endforeach; ?>            </select>        </div>        <div class="form_row">            <label>&nbsp;</label>            <input type="checkbox" name="add_another" id="add_another" value="TRUE" />			<label for="add_another" class="sub_label">Add another <?php echo ucwords(str_replace('_', ' ', $module_name)); ?></label>        </div>	</div>	<div class="form_row">		<label>&nbsp;</label>		<?php echo Form::submit('submit', '', array('class'=>'btn_add'));?>	</div><?php echo Form::close();?>