x -->		<?php else:?>		Tidak ada File di dalam <?php echo ucfirst($mediarelease->subject); ?>		<br/><br/>	<?php endif;?>	<div class="space1"></div>	<div id="wrapPagination">		<?php echo $pagination->render(); ?>	</div><!-- end wrap pagination -->	<div class="space20"></div>	</div><!-- end content --><script type="text/javascript">//<![CDATA[	<?php if (!empty($medias)) { 	$i_js = 1;	foreach ($medias as $media_js) {		if (!empty($mediafiles[$media_js->id])) {			foreach ($mediafiles[$media_js->id] as $mfiles_js) {				if (strstr($mfiles_js->file_name,'mp3')) {					if(file_exists($mediafile_upload_path . $mfiles_js->file_name)) { ?>$("#jquery_jplayer_<?php echo $i_js;?>").jPlayer({	ready: function () {		$(this).jPlayer("setMedia", { mp3: "<?php echo URL::site() . $mediafile_upload_url . $mfiles_js->file_name;?>" });	},	play: function() { // To avoid both jPlayers playing together.		$(this).jPlayer("pauseOthers");	},	preload: "none",	solution: "flash, html",	swfPath: "<?php echo ASSETS;?>js/library/jPlayer",	supplied: "mp3",	cssSelectorAncestor: "#jp_container_<?php echo $i_js;?>",	wmode: "window"});					<?php					} 				}			}		}		$i_js++;	}}?>	//]]></script>