<?phpclass Controller_Backend_AjaxMenu extends Controller_Backend_BaseAdmin {	public function before () {		parent::before();		$this->session = Session::instance();	}	public function action_index () {		if($_POST) {			if(isset($_POST['menu'])) {				$this->session->set('menu', $_POST['menu']);			}			if(isset($_POST['current'])) {				$this->session->set('current', $_POST['current']);			}		}	}}