<?php defined('SYSPATH') or die('No direct script access.'); ?><script type="text/javascript">    $(document).ready(function(){        $('input#logusername').focus(function(){            var email = $(this); var password = $('input#logpassword');            if (email.val() == '<?php echo i18n::get('username'); ?>') { email.val('').removeClass('disactive'); }             if (password.val()=='<?php echo i18n::get('password'); ?>'){ password.val('').removeClass('disactive'); }         });        $('input#logusername').blur(function(){            var email = $(this); var password = $('input#logpassword');            if (email.val() == '') { email.val('<?php echo i18n::get('username'); ?>'); email.addClass('disactive');}             if (password.val()==''){ password.val('<?php echo i18n::get('password'); ?>'); password.addClass('disactive'); }             });        $('input#logpassword').focus(function(){            var email = $('input#logusername'); var password = $(this);            if (email.val() == '<?php echo i18n::get('username'); ?>') { email.val('').removeClass('disactive'); }             if (password.val()=='<?php echo i18n::get('password'); ?>'){ password.val('').removeClass('disactive'); }         });        $('input#logpassword').blur(function(){            var password = $(this); var email = $('input#logusername');            if (email.val() == '') { email.val('<?php echo i18n::get('username'); ?>'); email.addClass('disactive');}             if (password.val()==''){ password.val('<?php echo i18n::get('password'); ?>'); password.addClass('disactive'); }          });    })</script><div style="width: 100%;margin:0 auto;" class="center">	<div class="fixed_login">		<div style="background:none;">			<a href="<?php echo URL::site();?>" title="" target="_blank">				<img alt="" style="margin:0 auto;" src="<?php echo IMG . THM;?>material/logo.png"/>			</a>		</div>		<div class="login_container" id="authentication"> 			<?php echo Form::open(URL::site()."admin-cp/authentication/validate", array('method'=>'POST','id'=>'login_admin','class'=>'auto_valid general'));?>				<label class="fl left loginLabel">User Id</label>				<div class="fl left form_row">					<input type="text" name="username" id="logusername" class="required <?php echo empty($_POST['username']) ? 'disactive' : ''; ?>" 						   value="<?php echo empty($_POST['username']) ? i18n::get('username') : $_POST['username']; ?>" style="width: 132px;"/>				</div>				<div class="clear ls12"></div>				<label class="fl left loginLabel">Password</label>				<div class="fl left form_row">					<input type="password" name="password" id="logpassword" class="required <?php echo empty($_POST['email']) ? 'disactive':''; ?>" 						   value="<?php echo empty($_POST['password']) ? i18n::get('password') : $_POST['password'];?>" style="width: 132px;" />				</div>				<div class="clear ls12"></div>				<div class="right" style="padding: 2px 0 0 0px;">				<input type="image" class="btn btn-primary" value="Login" src="<?php echo IMG.'admin/login_button.png';?>" style="width: 80px;"/>				</div>				<?php echo !empty($errors['username']) ? '<div class="left auth_error">'.$errors['username'].'</div>' :''; ?>                <?php echo !empty($errors['password']) ? '<div class="left auth_error">'.$errors['password'].'</div>' :''; ?>                <?php echo !empty($errors['error_login']) ? '<div class="left auth_error">'.$errors['error_login'].'</div>' :''; ?>				<div class="clear ls1"></div>			<?php echo Form::close();?>    		</div>	</div></div>